contracts/lending/CollateralManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../oracles/OracleAdapter.sol";
import "./DebtToken.sol";

/// @title CollateralManager - Collateral Management and Liquidation System
/// @notice Manages user collateral deposits, calculates collateral values using oracle prices, and handles liquidations
/// @dev Integrates with OracleAdapter for price feeds and DebtToken for debt tracking
/// @author CrosFi Team
contract CollateralManager is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    // State variables
    mapping(address => mapping(address => uint256)) public userCollateral; // user => token => amount
    mapping(address => bool) public approvedCollateralTokens; // token => approved status
    
    OracleAdapter public oracle;
    DebtToken public debtToken;
    
    uint256 public liquidationThreshold; // e.g., 7500 = 75%
    uint256 public liquidationBonus;     // e.g., 500 = 5%
    uint256 public constant BPS_DIVISOR = 10000;
    uint256 public constant PRECISION = 1e18;

    // Events
    event CollateralAdded(address indexed user, address indexed token, uint256 amount);
    event CollateralRemoved(address indexed user, address indexed token, uint256 amount);
    event LiquidationExecuted(
        address indexed borrower,
        address indexed liquidator,
        address indexed repayToken,
        uint256 repayAmount,
        address collateralToken,
        uint256 collateralSeized
    );
    event CollateralTokenStatusUpdated(address indexed token, bool status);
    event LiquidationThresholdUpdated(uint256 newThreshold);
    event LiquidationBonusUpdated(uint256 newBonus);

    // Modifiers
    modifier validAddress(address addr) {
        require(addr != address(0), "CollateralManager: invalid address");
        _;
    }

    modifier validAmount(uint256 amount) {
        require(amount > 0, "CollateralManager: amount must be greater than zero");
        _;
    }

    modifier onlyApprovedCollateral(address token) {
        require(approvedCollateralTokens[token], "CollateralManager: token not approved as collateral");
        _;
    }

    /**
     * @notice Constructor to initialize the collateral manager
     * @param _oracle Address of the OracleAdapter contract
     * @param _debtToken Address of the DebtToken contract
     * @param _liquidationThreshold Liquidation threshold in basis points (e.g., 7500 = 75%)
     * @param _liquidationBonus Liquidation bonus in basis points (e.g., 500 = 5%)
     */
    constructor(
        address _oracle,
        address _debtToken,
        uint256 _liquidationThreshold,
        uint256 _liquidationBonus
    ) validAddress(_oracle) validAddress(_debtToken) {
        require(_liquidationThreshold <= BPS_DIVISOR, "CollateralManager: liquidation threshold too high");
        require(_liquidationBonus <= BPS_DIVISOR, "CollateralManager: liquidation bonus too high");
        
        oracle = OracleAdapter(_oracle);
        debtToken = DebtToken(_debtToken);
        liquidationThreshold = _liquidationThreshold;
        liquidationBonus = _liquidationBonus;
    }

    /**
     * @notice Add collateral tokens to the user's collateral balance
     * @param token The token address to add as collateral (zero address for native CELO)
     * @param amount The amount of tokens to add
     */
    function addCollateral(address token, uint256 amount) 
        external 
        payable
        nonReentrant 
        onlyApprovedCollateral(token) 
        validAmount(amount) 
    {
        if (token == address(0)) {
            // Handle native CELO
            require(msg.value == amount, "CollateralManager: incorrect CELO amount");
        } else {
            // Handle ERC20 tokens
            IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        }
        
        // Update user's collateral balance
        userCollateral[msg.sender][token] += amount;
        
        emit CollateralAdded(msg.sender, token, amount);
    }

    /**
     * @notice Remove collateral tokens from the user's collateral balance
     * @param token The token address to remove from collateral
     * @param amount The amount of tokens to remove
     */
    function removeCollateral(address token, uint256 amount) 
        external 
        nonReentrant 
        validAmount(amount) 
    {
        require(userCollateral[msg.sender][token] >= amount, "CollateralManager: insufficient collateral");
        
        // Check if removal would make user undercollateralized
        uint256 userDebt = debtToken.getAccruedDebt(msg.sender);
        if (userDebt > 0) {
            uint256 currentCollateralValue = getTotalCollateralValue(msg.sender, token);
            uint256 collateralToRemoveValue = getCollateralValue(msg.sender, token, token);
            uint256 remainingCollateralValue = currentCollateralValue > collateralToRemoveValue ? 
                currentCollateralValue - collateralToRemoveValue : 0;
            require(
                remainingCollateralValue * BPS_DIVISOR >= userDebt * liquidationThreshold,
                "CollateralManager: removal would make position undercollateralized"
            );
        }
        
        // Update user's collateral balance
        userCollateral[msg.sender][token] -= amount;
        
        // Transfer tokens back to user
        if (token == address(0)) {
            // Handle native CELO
            (bool success, ) = payable(msg.sender).call{value: amount}("");
            require(success, "CollateralManager: CELO transfer failed");
        } else {
            // Handle ERC20 tokens
            IERC20(token).safeTransfer(msg.sender, amount);
        }
        
        emit CollateralRemoved(msg.sender, token, amount);
    }

    /**
     * @notice Get the value of a user's collateral in a specific denomination token
     * @param user The user address
     * @param token The collateral token address
     * @param denomToken The denomination token address
     * @return The collateral value in denomToken terms
     */
    function getCollateralValue(
        address user,
        address token,
        address denomToken
    ) public view returns (uint256) {
        uint256 amount = userCollateral[user][token];
        if (amount == 0) {
            return 0;
        }
        
        // If same token, no conversion needed
        if (token == denomToken) {
            return amount;
        }
        
        // Get exchange rate from oracle
        uint256 rate = oracle.getExchangeRate(token, denomToken);
        return (amount * rate) / PRECISION;
    }

    /**
     * @notice Get the total value of a user's collateral in a specific denomination token
     * @param user The user address
     * @param denomToken The denomination token address
     * @return The total collateral value in denomToken terms
     */
    function getTotalCollateralValue(
        address user,
        address denomToken
    ) public view returns (uint256) {
        uint256 totalValue = 0;
        
        // Note: In a production system, you would maintain a list of approved tokens
        // For now, we'll check common tokens. This could be optimized with a token registry.
        address[] memory commonTokens = new address[](3);
        commonTokens[0] = 0x874069fa1ee493706DbeE6Cf34FF9829832E6A00; // cUSD
        commonTokens[1] = 0x62b8b11039FF5064145D0D87d32C658DA4cC2Dc1; // USDC
        commonTokens[2] = 0x0000000000000000000000000000000000000000; // CELO (native)
        
        for (uint256 i = 0; i < commonTokens.length; i++) {
            if (approvedCollateralTokens[commonTokens[i]]) {
                totalValue += getCollateralValue(user, commonTokens[i], denomToken);
            }
        }
        
        return totalValue;
    }

    /**
     * @notice Get the total value of a user's collateral for specific tokens
     * @param user The user address
     * @param tokens Array of token addresses to check
     * @param denomToken The denomination token address
     * @return The total collateral value in denomToken terms
     */
    function getTotalCollateralValueForTokens(
        address user,
        address[] memory tokens,
        address denomToken
    ) public view returns (uint256) {
        uint256 totalValue = 0;
        
        for (uint256 i = 0; i < tokens.length; i++) {
            if (approvedCollateralTokens[tokens[i]]) {
                totalValue += getCollateralValue(user, tokens[i], denomToken);
            }
        }
        
        return totalValue;
    }

    /**
     * @notice Check if a user's position is liquidatable
     * @param user The user address
     * @param borrowToken The token the user borrowed
     * @return True if the position is liquidatable
     */
    function isLiquidatable(
        address user,
        address borrowToken
    ) public view returns (bool) {
        uint256 debtValue = debtToken.getAccruedDebt(user);
        if (debtValue == 0) {
            return false; // No debt = not liquidatable
        }
        
        uint256 collateralValue = getTotalCollateralValue(user, borrowToken);
        uint256 healthFactor = (collateralValue * BPS_DIVISOR) / debtValue;
        
        return healthFactor < liquidationThreshold;
    }

    /**
     * @notice Check if a user's position is liquidatable for specific tokens
     * @param user The user address
     * @param borrowToken The token the user borrowed
     * @param collateralTokens Array of collateral token addresses to check
     * @return True if the position is liquidatable
     */
    function isLiquidatableForTokens(
        address user,
        address borrowToken,
        address[] memory collateralTokens
    ) public view returns (bool) {
        uint256 debtValue = debtToken.getAccruedDebt(user);
        if (debtValue == 0) {
            return false; // No debt = not liquidatable
        }
        
        uint256 collateralValue = getTotalCollateralValueForTokens(user, collateralTokens, borrowToken);
        uint256 healthFactor = (collateralValue * BPS_DIVISOR) / debtValue;
        
        return healthFactor < liquidationThreshold;
    }

    /**
     * @notice Liquidate an undercollateralized position
     * @param borrower The borrower address to liquidate
     * @param repayToken The token to repay (debt token)
     * @param collateralToken The collateral token to seize
     * @param repayAmount The amount of debt to repay
     */
    function liquidate(
        address borrower,
        address repayToken,
        address collateralToken,
        uint256 repayAmount
    ) external nonReentrant validAmount(repayAmount) onlyApprovedCollateral(collateralToken) {
        require(isLiquidatable(borrower, repayToken), "CollateralManager: position not liquidatable");
        
        // Calculate collateral to seize
        uint256 rate = oracle.getExchangeRate(repayToken, collateralToken);
        uint256 baseCollateral = (repayAmount * rate) / PRECISION;
        uint256 collateralSeized = baseCollateral * (BPS_DIVISOR + liquidationBonus) / BPS_DIVISOR;
        
        require(
            userCollateral[borrower][collateralToken] >= collateralSeized,
            "CollateralManager: insufficient collateral to seize"
        );
        
        // Transfer repay tokens from liquidator to contract
        IERC20(repayToken).safeTransferFrom(msg.sender, address(this), repayAmount);
        
        // Burn debt from borrower
        debtToken.burnDebt(borrower, repayAmount);
        
        // Update borrower's collateral
        userCollateral[borrower][collateralToken] -= collateralSeized;
        
        // Transfer seized collateral to liquidator
        if (collateralToken == address(0)) {
            // Handle native CELO
            (bool success, ) = payable(msg.sender).call{value: collateralSeized}("");
            require(success, "CollateralManager: CELO transfer failed");
        } else {
            // Handle ERC20 tokens
            IERC20(collateralToken).safeTransfer(msg.sender, collateralSeized);
        }
        
        emit LiquidationExecuted(borrower, msg.sender, repayToken, repayAmount, collateralToken, collateralSeized);
    }

    /**
     * @notice Liquidate an undercollateralized position (for testing with specific collateral tokens)
     * @param borrower The borrower address to liquidate
     * @param repayToken The token to repay (debt token)
     * @param collateralToken The collateral token to seize
     * @param repayAmount The amount of debt to repay
     * @param collateralTokens Array of collateral tokens to check for liquidation eligibility
     */
    function liquidateForTokens(
        address borrower,
        address repayToken,
        address collateralToken,
        uint256 repayAmount,
        address[] memory collateralTokens
    ) external nonReentrant validAmount(repayAmount) onlyApprovedCollateral(collateralToken) {
        require(isLiquidatableForTokens(borrower, repayToken, collateralTokens), "CollateralManager: position not liquidatable");
        
        // Calculate collateral to seize
        uint256 rate = oracle.getExchangeRate(repayToken, collateralToken);
        uint256 baseCollateral = (repayAmount * rate) / PRECISION;
        uint256 collateralSeized = baseCollateral * (BPS_DIVISOR + liquidationBonus) / BPS_DIVISOR;
        
        require(
            userCollateral[borrower][collateralToken] >= collateralSeized,
            "CollateralManager: insufficient collateral to seize"
        );
        
        // Transfer repay tokens from liquidator to contract
        IERC20(repayToken).safeTransferFrom(msg.sender, address(this), repayAmount);
        
        // Burn debt from borrower
        debtToken.burnDebt(borrower, repayAmount);
        
        // Update borrower's collateral
        userCollateral[borrower][collateralToken] -= collateralSeized;
        
        // Transfer seized collateral to liquidator
        if (collateralToken == address(0)) {
            // Handle native CELO
            (bool success, ) = payable(msg.sender).call{value: collateralSeized}("");
            require(success, "CollateralManager: CELO transfer failed");
        } else {
            // Handle ERC20 tokens
            IERC20(collateralToken).safeTransfer(msg.sender, collateralSeized);
        }
        
        emit LiquidationExecuted(borrower, msg.sender, repayToken, repayAmount, collateralToken, collateralSeized);
    }

    /**
     * @notice Set the approved status of a collateral token (only owner)
     * @param token The token address (can be zero address for native CELO)
     * @param status True to approve, false to disapprove
     */
    function setApprovedCollateralToken(address token, bool status) 
        external 
        onlyOwner 
    {
        // Allow zero address for native CELO
        approvedCollateralTokens[token] = status;
        emit CollateralTokenStatusUpdated(token, status);
    }

    /**
     * @notice Set the liquidation threshold (only owner)
     * @param newThreshold The new liquidation threshold in basis points
     */
    function setLiquidationThreshold(uint256 newThreshold) external onlyOwner {
        require(newThreshold <= BPS_DIVISOR, "CollateralManager: liquidation threshold too high");
        liquidationThreshold = newThreshold;
        emit LiquidationThresholdUpdated(newThreshold);
    }

    /**
     * @notice Set the liquidation bonus (only owner)
     * @param newBonus The new liquidation bonus in basis points
     */
    function setLiquidationBonus(uint256 newBonus) external onlyOwner {
        require(newBonus <= BPS_DIVISOR, "CollateralManager: liquidation bonus too high");
        liquidationBonus = newBonus;
        emit LiquidationBonusUpdated(newBonus);
    }

    /**
     * @notice Get all collateral tokens and amounts for a user
     * @param user The user address
     * @return tokens Array of token addresses
     * @return amounts Array of corresponding amounts
     */
    function getUserCollateralTokens(address user) 
        external 
        view 
        returns (address[] memory tokens, uint256[] memory amounts) 
    {
        address[] memory commonTokens = new address[](3);
        commonTokens[0] = 0x874069fa1ee493706DbeE6Cf34FF9829832E6A00; // cUSD
        commonTokens[1] = 0x62b8b11039FF5064145D0D87d32C658DA4cC2Dc1; // USDC
        commonTokens[2] = 0x0000000000000000000000000000000000000000; // CELO (native)
        
        // Count non-zero balances
        uint256 count = 0;
        for (uint256 i = 0; i < commonTokens.length; i++) {
            if (userCollateral[user][commonTokens[i]] > 0) {
                count++;
            }
        }
        
        // Create arrays
        tokens = new address[](count);
        amounts = new uint256[](count);
        
        // Fill arrays
        uint256 index = 0;
        for (uint256 i = 0; i < commonTokens.length; i++) {
            if (userCollateral[user][commonTokens[i]] > 0) {
                tokens[index] = commonTokens[i];
                amounts[index] = userCollateral[user][commonTokens[i]];
                index++;
            }
        }
    }

    /**
     * @notice Get collateral tokens and amounts for a user for specific tokens
     * @param user The user address
     * @param tokenList Array of token addresses to check
     * @return tokens Array of token addresses with non-zero balances
     * @return amounts Array of corresponding amounts
     */
    function getUserCollateralTokensForList(address user, address[] memory tokenList) 
        external 
        view 
        returns (address[] memory tokens, uint256[] memory amounts) 
    {
        // Count non-zero balances
        uint256 count = 0;
        for (uint256 i = 0; i < tokenList.length; i++) {
            if (userCollateral[user][tokenList[i]] > 0) {
                count++;
            }
        }
        
        // Create arrays
        tokens = new address[](count);
        amounts = new uint256[](count);
        
        // Fill arrays
        uint256 index = 0;
        for (uint256 i = 0; i < tokenList.length; i++) {
            if (userCollateral[user][tokenList[i]] > 0) {
                tokens[index] = tokenList[i];
                amounts[index] = userCollateral[user][tokenList[i]];
                index++;
            }
        }
    }

    /**
     * @notice Get the health factor for a user's position
     * @param user The user address
     * @param borrowToken The token the user borrowed
     * @return The health factor (collateral value / debt value) in basis points
     */
    function getHealthFactor(address user, address borrowToken) external view returns (uint256) {
        uint256 debtValue = debtToken.getAccruedDebt(user);
        if (debtValue == 0) {
            return type(uint256).max; // No debt = infinite health factor
        }
        
        uint256 collateralValue = getTotalCollateralValue(user, borrowToken);
        return (collateralValue * BPS_DIVISOR) / debtValue;
    }

    /**
     * @notice Get the health factor for a user's position with specific collateral tokens
     * @param user The user address
     * @param borrowToken The token the user borrowed
     * @param collateralTokens Array of collateral token addresses to check
     * @return The health factor (collateral value / debt value) in basis points
     */
    function getHealthFactorForTokens(
        address user,
        address borrowToken,
        address[] memory collateralTokens
    ) external view returns (uint256) {
        uint256 debtValue = debtToken.getAccruedDebt(user);
        if (debtValue == 0) {
            return type(uint256).max; // No debt = infinite health factor
        }
        
        uint256 collateralValue = getTotalCollateralValueForTokens(user, collateralTokens, borrowToken);
        return (collateralValue * BPS_DIVISOR) / debtValue;
    }

    /**
     * @notice Helper function for testing - mint debt tokens (only for testing)
     * @param borrower The borrower address
     * @param amount The amount to mint
     */
    function mintDebtForTesting(address borrower, uint256 amount) external onlyOwner {
        debtToken.mintDebt(borrower, amount);
    }

    // TODO: Add partial liquidation support
    // TODO: Implement multi-collateral liquidation
    // TODO: Add liquidation protection period for new positions
    // TODO: Implement dynamic liquidation bonus based on market conditions
    // TODO: Add emergency pause functionality
}

contracts/lending/DebtToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/// @title DebtToken - Non-transferable debt representation token
/// @notice Represents a borrower's outstanding loan amount for a specific asset
/// @dev Inherits from ERC20 but overrides transfer functions to make it non-transferable
/// @author CrosFi Team
contract DebtToken is ERC20, Ownable {
    // State variables
    address public lendingPool; // The authorized pool that can mint/burn
    mapping(address => uint256) public lastAccrualTimestamp; // Per borrower timestamp
    uint256 public annualInterestRate; // In basis points (bps)
    uint256 public constant BPS_DIVISOR = 10000; // 100% = 10000 bps

    // Events
    event DebtMinted(address indexed borrower, uint256 amount);
    event DebtBurned(address indexed borrower, uint256 amount);
    event InterestAccrued(address indexed borrower, uint256 interest);
    event InterestRateUpdated(uint256 newRate);
    event LendingPoolUpdated(address indexed newPool);

    // Modifiers
    modifier onlyLendingPool() {
        require(msg.sender == lendingPool, "DebtToken: caller is not lending pool");
        _;
    }

    modifier validAddress(address addr) {
        require(addr != address(0), "DebtToken: invalid address");
        _;
    }

    modifier validAmount(uint256 amount) {
        require(amount > 0, "DebtToken: amount must be greater than zero");
        _;
    }

    /**
     * @notice Constructor to initialize the debt token
     * @param name_ The name of the debt token
     * @param symbol_ The symbol of the debt token
     * @param _annualInterestRate The annual interest rate in basis points (e.g., 500 = 5%)
     */
    constructor(
        string memory name_,
        string memory symbol_,
        uint256 _annualInterestRate
    ) ERC20(name_, symbol_) {
        require(_annualInterestRate <= BPS_DIVISOR, "DebtToken: interest rate too high");
        
        lendingPool = msg.sender;
        annualInterestRate = _annualInterestRate;
    }

    /**
     * @notice Mint debt tokens to a borrower (only callable by lending pool)
     * @param borrower The address of the borrower
     * @param amount The amount of debt to mint
     */
    function mintDebt(address borrower, uint256 amount) 
        external 
        onlyLendingPool 
        validAddress(borrower) 
        validAmount(amount) 
    {
        // Accrue interest before minting new debt
        accrueInterest(borrower);
        
        // Mint the debt tokens to the borrower
        _mint(borrower, amount);
        
        // Update the last accrual timestamp
        lastAccrualTimestamp[borrower] = block.timestamp;
        
        emit DebtMinted(borrower, amount);
    }

    /**
     * @notice Burn debt tokens from a borrower (only callable by lending pool)
     * @param borrower The address of the borrower
     * @param amount The amount of debt to burn
     */
    function burnDebt(address borrower, uint256 amount) 
        external 
        onlyLendingPool 
        validAddress(borrower) 
        validAmount(amount) 
    {
        // Accrue interest before burning debt
        accrueInterest(borrower);
        
        // Check if borrower has sufficient debt balance
        require(balanceOf(borrower) >= amount, "DebtToken: insufficient debt balance");
        
        // Burn the debt tokens from the borrower
        _burn(borrower, amount);
        
        // Update the last accrual timestamp
        lastAccrualTimestamp[borrower] = block.timestamp;
        
        emit DebtBurned(borrower, amount);
    }

    /**
     * @notice Accrue interest for a borrower based on time elapsed
     * @param borrower The address of the borrower
     */
    function accrueInterest(address borrower) public validAddress(borrower) {
        uint256 currentBalance = balanceOf(borrower);
        
        // If no balance, no interest to accrue
        if (currentBalance == 0) {
            lastAccrualTimestamp[borrower] = block.timestamp;
            return;
        }
        
        uint256 lastTimestamp = lastAccrualTimestamp[borrower];
        
        // If this is the first time, set timestamp and return
        if (lastTimestamp == 0) {
            lastAccrualTimestamp[borrower] = block.timestamp;
            return;
        }
        
        uint256 timeDelta = block.timestamp - lastTimestamp;
        
        // If no time has passed, no interest to accrue
        if (timeDelta == 0) {
            return;
        }
        
        // Calculate interest: balance * rate * time / (365 days * BPS_DIVISOR)
        uint256 interest = (currentBalance * annualInterestRate * timeDelta) / (365 days * BPS_DIVISOR);
        
        // Only mint interest if it's greater than 0
        if (interest > 0) {
            _mint(borrower, interest);
            emit InterestAccrued(borrower, interest);
        }
        
        // Update the last accrual timestamp
        lastAccrualTimestamp[borrower] = block.timestamp;
    }

    /**
     * @notice Get the total accrued debt for a borrower (including pending interest)
     * @param borrower The address of the borrower
     * @return The total debt including accrued interest
     */
    function getAccruedDebt(address borrower) external view validAddress(borrower) returns (uint256) {
        uint256 currentBalance = balanceOf(borrower);
        
        // If no balance, no debt
        if (currentBalance == 0) {
            return 0;
        }
        
        uint256 lastTimestamp = lastAccrualTimestamp[borrower];
        
        // If no previous timestamp, return current balance
        if (lastTimestamp == 0) {
            return currentBalance;
        }
        
        uint256 timeDelta = block.timestamp - lastTimestamp;
        
        // If no time has passed, return current balance
        if (timeDelta == 0) {
            return currentBalance;
        }
        
        // Calculate pending interest without minting
        uint256 pendingInterest = (currentBalance * annualInterestRate * timeDelta) / (365 days * BPS_DIVISOR);
        
        return currentBalance + pendingInterest;
    }

    /**
     * @notice Set the annual interest rate (only callable by owner)
     * @param newRate The new annual interest rate in basis points
     */
    function setAnnualInterestRate(uint256 newRate) external onlyOwner {
        require(newRate <= BPS_DIVISOR, "DebtToken: interest rate too high");
        
        annualInterestRate = newRate;
        emit InterestRateUpdated(newRate);
    }

    /**
     * @notice Set the lending pool address (only callable by owner)
     * @param newPool The new lending pool address
     */
    function setLendingPool(address newPool) external onlyOwner validAddress(newPool) {
        lendingPool = newPool;
        emit LendingPoolUpdated(newPool);
    }

    // Override transfer functions to make the token non-transferable

    /**
     * @notice Override transfer function to prevent transfers
     * @dev This token is non-transferable
     */
    function transfer(address, uint256) public pure override returns (bool) {
        revert("DebtToken: non-transferable");
    }

    /**
     * @notice Override transferFrom function to prevent transfers
     * @dev This token is non-transferable
     */
    function transferFrom(address, address, uint256) public pure override returns (bool) {
        revert("DebtToken: non-transferable");
    }

    /**
     * @notice Override approve function to prevent approvals
     * @dev This token is non-transferable
     */
    function approve(address, uint256) public pure override returns (bool) {
        revert("DebtToken: non-transferable");
    }

    /**
     * @notice Override allowance function to always return 0
     * @dev This token is non-transferable
     */
    function allowance(address, address) public pure override returns (uint256) {
        return 0;
    }

    /**
     * @notice Override increaseAllowance function to prevent approvals
     * @dev This token is non-transferable
     */
    function increaseAllowance(address, uint256) public pure override returns (bool) {
        revert("DebtToken: non-transferable");
    }

    /**
     * @notice Override decreaseAllowance function to prevent approvals
     * @dev This token is non-transferable
     */
    function decreaseAllowance(address, uint256) public pure override returns (bool) {
        revert("DebtToken: non-transferable");
    }

    // TODO: Integrate with InterestModel for dynamic rates
    // TODO: Add compound interest calculation option
    // TODO: Implement interest rate curves based on utilization
    // TODO: Add flash loan protection
}

contracts/oracles/OracleAdapter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@chainlink/contracts/src/v0.8/shared/interfaces/AggregatorV3Interface.sol";

/// @title OracleAdapter - Price Feed Manager and Volatility Calculator
/// @notice Manages multiple price feeds, calculates volatility, and provides price deviation data
/// @dev Integrates with Chainlink oracles and supports historical price tracking for volatility calculation
/// @author CrosFi Team
contract OracleAdapter is Ownable {
    // Core data structures
    struct PriceFeed {
        address oracleAddress;
        uint8 decimals;
        bool isActive;
        uint256 lastPrice;
        uint256 lastUpdateTime;
    }

    struct HistoricalPrice {
        uint256 price;
        uint256 timestamp;
    }

    // State variables
    mapping(address => PriceFeed) public priceFeeds;

    // Historical prices for volatility calculation (circular buffer)
    mapping(address => mapping(uint256 => HistoricalPrice)) public historicalPrices;
    mapping(address => uint256) public currentPriceIndex; // Current position in circular buffer

    // Constants
    uint256 public constant HISTORY_SIZE = 10; // Store last 10 prices
    uint256 public constant PRECISION = 1e18;
    uint256 public constant MAX_PRICE_AGE = 1 hours;

    // Events
    event PriceFeedUpdated(address indexed token, address indexed oracle);
    event PriceUpdated(address indexed token, uint256 price);
    event PriceFeedDeactivated(address indexed token);

    // Modifiers
    modifier validAddress(address addr) {
        require(addr != address(0), "Invalid address");
        _;
    }

    modifier activePriceFeed(address token) {
        require(priceFeeds[token].isActive, "Price feed not active");
        _;
    }

    /**
     * @notice Sets or updates a price feed for a token
     * @param token The token address to set price feed for
     * @param oracle The oracle contract address
     * @param decimals The number of decimals the oracle returns
     */
    function setPriceFeed(address token, address oracle, uint8 decimals) 
        external 
        onlyOwner 
        validAddress(token) 
        validAddress(oracle) 
    {
        priceFeeds[token] = PriceFeed({
            oracleAddress: oracle,
            decimals: decimals,
            isActive: true,
            lastPrice: 0,
            lastUpdateTime: 0
        });

        emit PriceFeedUpdated(token, oracle);
    }

    /**
     * @notice Gets the latest price for a token in cUSD terms (1e18 precision)
     * @param token The token address to get price for
     * @return price The normalized price in 1e18 precision
     */
    function getPrice(address token) 
        external 
        view 
        activePriceFeed(token) 
        returns (uint256 price) 
    {
        AggregatorV3Interface oracle = AggregatorV3Interface(priceFeeds[token].oracleAddress);

        (
            ,
            int256 rawPrice,
            ,
            uint256 updatedAt,

        ) = oracle.latestRoundData();

        // Sanity checks
        require(rawPrice > 0, "Invalid price");
        require(updatedAt > 0, "Invalid timestamp");
        require(block.timestamp - updatedAt < MAX_PRICE_AGE, "Price too stale");

        // Normalize price to 1e18 precision
        price = _normalizePrice(rawPrice, priceFeeds[token].decimals);
    }

    /**
     * @notice Gets the exchange rate between two tokens
     * @param baseToken The base token address
     * @param quoteToken The quote token address
     * @return rate The exchange rate (baseToken / quoteToken) in 1e18 precision
     */
    function getExchangeRate(address baseToken, address quoteToken) 
        external 
        view 
        returns (uint256 rate) 
    {
        uint256 basePrice = this.getPrice(baseToken);
        uint256 quotePrice = this.getPrice(quoteToken);

        require(quotePrice > 0, "Quote price cannot be zero");

        rate = (basePrice * PRECISION) / quotePrice;
    }

    /**
     * @notice Calculates the volatility of a token based on historical prices
     * @param token The token address to calculate volatility for
     * @return volatility The volatility as a percentage (1e18 = 100%)
     */
    function getVolatility(address token) external view returns (uint256 volatility) {
        uint256[] memory prices = new uint256[](HISTORY_SIZE);
        uint256 validCount = 0;

        // Collect valid historical prices
        for (uint256 i = 0; i < HISTORY_SIZE; i++) {
            HistoricalPrice memory histPrice = historicalPrices[token][i];
            if (histPrice.price > 0 && histPrice.timestamp > 0) {
                prices[validCount] = histPrice.price;
                validCount++;
            }
        }

        // Need at least 2 samples for volatility calculation
        if (validCount < 2) {
            return 0;
        }

        // Resize array to valid count
        uint256[] memory validPrices = new uint256[](validCount);
        for (uint256 i = 0; i < validCount; i++) {
            validPrices[i] = prices[i];
        }

        volatility = _calculateStandardDeviation(validPrices);
    }

    /**
     * @notice Gets the price deviation between current and last stored price
     * @param token The token address to get deviation for
     * @return deviation The absolute percentage difference (1e18 = 100%)
     */
    function getPriceDeviation(address token) 
        external 
        view 
        activePriceFeed(token) 
        returns (uint256 deviation) 
    {
        uint256 currentPrice = this.getPrice(token);
        uint256 lastPrice = priceFeeds[token].lastPrice;

        if (lastPrice == 0) {
            return 0;
        }

        // Calculate absolute percentage difference
        if (currentPrice > lastPrice) {
            deviation = ((currentPrice - lastPrice) * PRECISION) / lastPrice;
        } else {
            deviation = ((lastPrice - currentPrice) * PRECISION) / lastPrice;
        }
    }

    /**
     * @notice Updates the price history for a token (can be called by anyone)
     * @param token The token address to update history for
     */
    function updatePriceHistory(address token) external activePriceFeed(token) {
        uint256 currentPrice = this.getPrice(token);
        uint256 currentIndex = currentPriceIndex[token];

        // Store in circular buffer
        historicalPrices[token][currentIndex] = HistoricalPrice({
            price: currentPrice,
            timestamp: block.timestamp
        });

        // Update index (circular)
        currentPriceIndex[token] = (currentIndex + 1) % HISTORY_SIZE;

        // Update last price and timestamp
        priceFeeds[token].lastPrice = currentPrice;
        priceFeeds[token].lastUpdateTime = block.timestamp;

        emit PriceUpdated(token, currentPrice);
    }

    /**
     * @notice Deactivates a price feed
     * @param token The token address to deactivate
     */
    function deactivatePriceFeed(address token) external onlyOwner {
        require(priceFeeds[token].isActive, "Price feed already inactive");
        priceFeeds[token].isActive = false;
        emit PriceFeedDeactivated(token);
    }

    /**
     * @notice Gets all historical prices for a token
     * @param token The token address to get history for
     * @return prices Array of historical prices
     */
    function getHistoricalPrices(address token) 
        external 
        view 
        returns (HistoricalPrice[] memory prices) 
    {
        prices = new HistoricalPrice[](HISTORY_SIZE);

        for (uint256 i = 0; i < HISTORY_SIZE; i++) {
            prices[i] = historicalPrices[token][i];
        }
    }

    /**
     * @notice Normalizes a price to 1e18 precision
     * @param price The raw price from oracle
     * @param decimals The number of decimals in the raw price
     * @return normalizedPrice The price in 1e18 precision
     */
    function _normalizePrice(int256 price, uint8 decimals) 
        internal 
        pure 
        returns (uint256 normalizedPrice) 
    {
        uint256 decimalFactor = 10 ** decimals;
        normalizedPrice = (uint256(price) * PRECISION) / decimalFactor;
    }

    /**
     * @notice Calculates the standard deviation of an array of prices
     * @param prices Array of prices to calculate standard deviation for
     * @return stdDev The standard deviation as a percentage (1e18 = 100%)
     */
    function _calculateStandardDeviation(uint256[] memory prices) 
        internal 
        pure 
        returns (uint256 stdDev) 
    {
        uint256 length = prices.length;
        if (length < 2) {
            return 0;
        }

        // Calculate mean
        uint256 sum = 0;
        for (uint256 i = 0; i < length; i++) {
            sum += prices[i];
        }
        uint256 mean = sum / length;

        // Calculate variance
        uint256 variance = 0;
        for (uint256 i = 0; i < length; i++) {
            uint256 diff = prices[i] > mean ? prices[i] - mean : mean - prices[i];
            variance += (diff * diff) / length;
        }

        // Calculate standard deviation (square root of variance)
        stdDev = _sqrt(variance);
    }

    /**
     * @notice Calculates the square root of a number using Babylonian method
     * @param x The number to calculate square root for
     * @return sqrt The square root of x
     */
    function _sqrt(uint256 x) internal pure returns (uint256 sqrt) {
        if (x == 0) return 0;

        uint256 z = (x + 1) / 2;
        uint256 y = x;

        while (z < y) {
            y = z;
            z = (x / z + z) / 2;
        }

        sqrt = y;
    }

    // TODO: Integrate Mento-specific oracle interface for native Celo token pairs
    // TODO: Add support for multiple oracle sources with price aggregation
    // TODO: Implement time-weighted average price (TWAP) calculation
    // TODO: Add circuit breaker for extreme price movements
}

