    uint80 public roundId;
    int256 public answer;
    uint256 public startedAt;
    uint256 public updatedAt;
    uint80 public answeredInRound;

    /**
     * @notice Sets the latest round data for testing
     * @param _roundId The round ID
     * @param _answer The price answer
     * @param _startedAt When the round started
     * @param _updatedAt When the round was updated
     * @param _answeredInRound The round in which the answer was computed
     */
