// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../libraries/TokenConfig.sol";

/**
 * @title LendingPool
 * @dev A lending pool contract that allows users to deposit, withdraw, borrow, and repay tokens
 * with collateralization requirements and health factor checks.
 */
contract LendingPool is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    // Mappings to track deposits and borrows
    mapping(address => mapping(address => uint256)) public deposits;
    mapping(address => mapping(address => uint256)) public borrows;
    mapping(address => mapping(address => uint256)) public collateral;
    mapping(address => uint256) public totalDeposits;
    mapping(address => uint256) public totalBorrows;

    // Address of the interest model contract
    address public interestModel;
    // Address of the oracle adapter contract
    address public oracleAdapter;

    // Events
    event Deposit(address indexed user, address indexed token, uint256 amount);
    event Withdraw(address indexed user, address indexed token, uint256 amount);
    event Borrow(
        address indexed user,
        address indexed collateralToken,
        address indexed borrowToken,
        uint256 collateralAmount,
        uint256 borrowAmount
    );
    event Repay(address indexed user, address indexed token, uint256 amount);
    event OracleUpdated(address indexed oldOracle, address indexed newOracle);
    event InterestModelUpdated(address indexed oldModel, address indexed newModel);

    // Modifiers
    modifier onlySupportedToken(address token) {
        require(TokenConfig.isSupportedToken(token), "Token not supported");
        _;
    }

    modifier validAmount(uint256 amount) {
        require(amount > 0, "Amount must be greater than 0");
        _;
    }

    /**
     * @dev Constructor to set the interest model address
     * @param _interestModel Address of the interest model contract
     */
    constructor(address _interestModel) {
        require(_interestModel != address(0), "Invalid interest model address");
        interestModel = _interestModel;
    }

    /**
     * @dev Deposit tokens into the lending pool
     * @param token Token address to deposit
     * @param amount Amount to deposit
     */
    function deposit(address token, uint256 amount) 
        external 
        nonReentrant 
        onlySupportedToken(token) 
        validAmount(amount) 
    {
        // Transfer tokens from user to contract
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);

        // Update user deposits and total deposits
        deposits[token][msg.sender] += amount;
        totalDeposits[token] += amount;

        emit Deposit(msg.sender, token, amount);
    }

    /**
     * @dev Withdraw tokens from the lending pool
     * @param token Token address to withdraw
     * @param amount Amount to withdraw
     */
    function withdraw(address token, uint256 amount) 
        external 
        nonReentrant 
        onlySupportedToken(token) 
        validAmount(amount) 
    {
        require(deposits[token][msg.sender] >= amount, "Insufficient balance");

        // Check if withdrawal would break health factor if user has borrows
        // TODO: Implement health factor check with oracle integration
        // require(calculateHealthFactor(msg.sender) >= 1.0, "Withdrawal would break health factor");

        // Update user deposits and total deposits
        deposits[token][msg.sender] -= amount;
        totalDeposits[token] -= amount;

        // Transfer tokens to user
        IERC20(token).safeTransfer(msg.sender, amount);

        emit Withdraw(msg.sender, token, amount);
    }

    /**
     * @dev Borrow tokens using collateral
     * @param collateralToken Token to use as collateral
     * @param borrowToken Token to borrow
     * @param collateralAmount Amount of collateral to provide
     * @param borrowAmount Amount to borrow
     */
    function borrow(
        address collateralToken,
        address borrowToken,
        uint256 collateralAmount,
        uint256 borrowAmount
    ) 
        external 
        nonReentrant 
        onlySupportedToken(collateralToken) 
        onlySupportedToken(borrowToken) 
        validAmount(collateralAmount) 
        validAmount(borrowAmount) 
    {
        // Transfer collateral from user to contract
        IERC20(collateralToken).safeTransferFrom(msg.sender, address(this), collateralAmount);

        // TODO: Integrate OracleAdapter for real-time price feeds
        // For now, using placeholder values (1:1 ratio)
        uint256 collateralValue = collateralAmount;
        uint256 borrowValue = borrowAmount;

        // Health factor check: collateralValue >= borrowValue * 1.5
        require(collateralValue >= (borrowValue * 150) / 100, "Insufficient collateral");

        // Check if pool has enough liquidity
        require(totalDeposits[borrowToken] >= borrowAmount, "Insufficient liquidity");

        // Update collateral and borrow mappings
        collateral[collateralToken][msg.sender] += collateralAmount;
        borrows[borrowToken][msg.sender] += borrowAmount;
        totalBorrows[borrowToken] += borrowAmount;

        // Transfer borrowed tokens to user
        IERC20(borrowToken).safeTransfer(msg.sender, borrowAmount);

        emit Borrow(msg.sender, collateralToken, borrowToken, collateralAmount, borrowAmount);
    }

    /**
     * @dev Repay borrowed tokens
     * @param token Token to repay
     * @param amount Amount to repay
     */
    function repay(address token, uint256 amount) 
        external 
        nonReentrant 
        onlySupportedToken(token) 
        validAmount(amount) 
    {
        require(borrows[token][msg.sender] >= amount, "Repay amount exceeds borrow balance");

        // Transfer tokens from user to contract
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);

        // Update borrow mappings
        borrows[token][msg.sender] -= amount;
        totalBorrows[token] -= amount;

        emit Repay(msg.sender, token, amount);
    }

    /**
     * @dev Get user's position (deposited and borrowed amounts)
     * @param user User address
     * @return deposited Array of deposited amounts for each supported token
     * @return borrowed Array of borrowed amounts for each supported token
     */
    function getUserPosition(address user) 
        external 
        view 
        returns (uint256[] memory deposited, uint256[] memory borrowed) 
    {
        address[] memory supportedTokens = TokenConfig.getAllSupportedTokens();
        uint256 tokenCount = supportedTokens.length;
        
        deposited = new uint256[](tokenCount);
        borrowed = new uint256[](tokenCount);
        
        for (uint256 i = 0; i < tokenCount; i++) {
            address token = supportedTokens[i];
            deposited[i] = deposits[token][user];
            borrowed[i] = borrows[token][user];
        }
    }

    /**
     * @dev Calculate health factor for a user
     * @param user User address
     * @return healthFactor Health factor (multiplied by 100 for precision)
     */
    function calculateHealthFactor(address user) external view returns (uint256 healthFactor) {
        // TODO: Integrate OracleAdapter for real-time price feeds
        // For now, return placeholder value
        uint256 totalCollateralValue = getTotalCollateralValue(user);
        uint256 totalBorrowValue = getTotalBorrowValue(user);
        
        if (totalBorrowValue == 0) {
            return type(uint256).max; // No borrows = infinite health factor
        }
        
        // Health factor formula: (totalCollateralValue * 100) / (totalBorrowValue * 150)
        healthFactor = (totalCollateralValue * 100) / (totalBorrowValue * 150);
    }

    /**
     * @dev Get total collateral value for a user (placeholder)
     * @param user User address
     * @return totalValue Total collateral value
     */
    function getTotalCollateralValue(address user) public view returns (uint256 totalValue) {
        // TODO: Integrate OracleAdapter for real-time price feeds
        address[] memory supportedTokens = TokenConfig.getAllSupportedTokens();
        
        for (uint256 i = 0; i < supportedTokens.length; i++) {
            address token = supportedTokens[i];
            totalValue += collateral[token][user]; // Placeholder: 1:1 ratio
        }
    }

    /**
     * @dev Get total borrow value for a user (placeholder)
     * @param user User address
     * @return totalValue Total borrow value
     */
    function getTotalBorrowValue(address user) public view returns (uint256 totalValue) {
        // TODO: Integrate OracleAdapter for real-time price feeds
        address[] memory supportedTokens = TokenConfig.getAllSupportedTokens();
        
        for (uint256 i = 0; i < supportedTokens.length; i++) {
            address token = supportedTokens[i];
            totalValue += borrows[token][user]; // Placeholder: 1:1 ratio
        }
    }

    /**
     * @dev Calculate utilization rate for a token
     * @param token Token address
     * @return utilizationRate Utilization rate (multiplied by 100 for precision)
     */
    function getUtilizationRate(address token) external view returns (uint256 utilizationRate) {
        uint256 totalSupply = totalDeposits[token];
        if (totalSupply == 0) {
            return 0;
        }
        
        utilizationRate = (totalBorrows[token] * 100) / totalSupply;
    }

    /**
     * @dev Set oracle adapter address (owner only)
     * @param _oracle New oracle adapter address
     */
    function setOracleAdapter(address _oracle) external onlyOwner {
        require(_oracle != address(0), "Invalid oracle address");
        address oldOracle = oracleAdapter;
        oracleAdapter = _oracle;
        emit OracleUpdated(oldOracle, _oracle);
    }

    /**
     * @dev Set interest model address (owner only)
     * @param _model New interest model address
     */
    function setInterestModel(address _model) external onlyOwner {
        require(_model != address(0), "Invalid interest model address");
        address oldModel = interestModel;
        interestModel = _model;
        emit InterestModelUpdated(oldModel, _model);
    }

    // TODO: Integrate OracleAdapter for real-time price feeds
    // TODO: Implement InterestModel for dynamic interest rate calculations
    // TODO: Add liquidation mechanism when health factor < 1.0
    // TODO: Implement interest accrual on borrows
    // TODO: Add supply/borrow caps per token
}

