contracts/libraries/TokenConfig.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title TokenConfig
 * @dev Configuration for supported tokens on Celo Alfajores testnet
 */
library TokenConfig {
    struct TokenInfo {
        address tokenAddress;
        string symbol;
        string name;
        uint8 decimals;
        bool isNative; // true for CELO, false for ERC20 tokens
        uint256 maxDeposit; // Maximum deposit amount per user
        uint256 minDeposit; // Minimum deposit amount
        bool isActive; // Whether token is currently supported
    }

// Local Hardhat testnet token addresses (mock tokens)
address public constant CUSD_ADDRESS = 0x40a42Baf86Fc821f972Ad2aC878729063CeEF403;
address public constant USDC_ADDRESS = 0x96F3Ce39Ad2BfDCf92C0F6E2C2CAbF83874660Fc;
address public constant CELO_ADDRESS = address(0); // Native token

    // Token configurations
    function getTokenInfo(address token) internal pure returns (TokenInfo memory) {
        if (token == CUSD_ADDRESS) {
            return TokenInfo({
                tokenAddress: CUSD_ADDRESS,
                symbol: "cUSD",
                name: "Celo Dollar",
                decimals: 18,
                isNative: false,
                maxDeposit: 1000000 * 10**18, // 1M cUSD
                minDeposit: 1 * 10**18, // 1 cUSD
                isActive: true
            });
        } else if (token == USDC_ADDRESS) {
            return TokenInfo({
                tokenAddress: USDC_ADDRESS,
                symbol: "USDC",
                name: "USD Coin",
                decimals: 6,
                isNative: false,
                maxDeposit: 1000000 * 10**6, // 1M USDC
                minDeposit: 1 * 10**6, // 1 USDC
                isActive: true
            });
        } else if (token == CELO_ADDRESS) {
            return TokenInfo({
                tokenAddress: CELO_ADDRESS,
                symbol: "CELO",
                name: "Celo",
                decimals: 18,
                isNative: true,
                maxDeposit: 100000 * 10**18, // 100K CELO
                minDeposit: 1 * 10**18, // 1 CELO
                isActive: true
            });
        } else {
            revert("TokenConfig: Unsupported token");
        }
    }

    function isSupportedToken(address token) internal pure returns (bool) {
        return token == CUSD_ADDRESS || token == USDC_ADDRESS || token == CELO_ADDRESS;
    }

    function getAllSupportedTokens() internal pure returns (address[] memory) {
        address[] memory tokens = new address[](3);
        tokens[0] = CUSD_ADDRESS;
        tokens[1] = USDC_ADDRESS;
        tokens[2] = CELO_ADDRESS;
        return tokens;
    }

    function getTokenSymbol(address token) internal pure returns (string memory) {
        return getTokenInfo(token).symbol;
    }

    function getTokenDecimals(address token) internal pure returns (uint8) {
        return getTokenInfo(token).decimals;
    }

    function isNativeToken(address token) internal pure returns (bool) {
        return getTokenInfo(token).isNative;
    }
}

contracts/strategies/MentoYieldStrategy..content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "../libraries/TokenConfig.sol";

/**
 * @title MentoYieldStrategy
 * @dev Strategy that generates yield through Mento protocol interactions
 * This is a simplified implementation for the hackathon demo
 */
contract MentoYieldStrategy is ReentrancyGuard, Ownable {
    using SafeMath for uint256;

    // Events
    event Deposited(address indexed token, uint256 amount);
    event Withdrawn(address indexed token, uint256 amount);
    event YieldGenerated(address indexed token, uint256 yieldAmount);
    event Rebalanced(address indexed fromToken, address indexed toToken, uint256 amount);

    // State variables
    mapping(address => uint256) public tokenDeposits; // token => total deposited
    mapping(address => uint256) public tokenWithdrawals; // token => total withdrawn
    mapping(address => uint256) public tokenYield; // token => total yield generated
    
    uint256 public constant APY_BASIS_POINTS = 800; // 8% APY
    uint256 public constant SECONDS_PER_YEAR = 365 days;
    uint256 public lastYieldCalculation;
    
    // Mento protocol addresses (placeholder - would be real addresses in production)
    address public constant MENTO_RESERVE = address(0x1234567890123456789012345678901234567890);
    
    // Modifiers
    modifier onlyVault() {
        require(msg.sender == vault, "Only vault can call this function");
        _;
    }

    modifier onlyWhitelistedToken(address token) {
        require(TokenConfig.isSupportedToken(token), "Token not supported");
        _;
    }

    address public vault;

    constructor() {
        lastYieldCalculation = block.timestamp;
    }

    /**
     * @dev Set the vault address (can only be called once)
     * @param _vault Vault contract address
     */
    function setVault(address _vault) external onlyOwner {
        require(vault == address(0), "Vault already set");
        require(_vault != address(0), "Invalid vault address");
        vault = _vault;
    }

    /**
     * @dev Deposit tokens into the strategy
     * @param token Token address
     * @param amount Amount to deposit
     */
    function deposit(address token, uint256 amount) 
        external 
        onlyVault 
        onlyWhitelistedToken(token)
        nonReentrant 
    {
        require(amount > 0, "Amount must be greater than 0");
        
        // Update deposits
        tokenDeposits[token] = tokenDeposits[token].add(amount);
        
        // In a real implementation, this would:
        // 1. Swap tokens through Mento protocol for optimal yield
        // 2. Provide liquidity to Mento pools
        // 3. Stake tokens in yield-generating protocols
        
        // For demo purposes, we just track the deposit
        emit Deposited(token, amount);
    }

    /**
     * @dev Withdraw tokens from the strategy
     * @param token Token address
     * @param amount Amount to withdraw
     */
    function withdraw(address token, uint256 amount) 
        external 
        onlyVault 
        onlyWhitelistedToken(token)
        nonReentrant 
    {
        require(amount > 0, "Amount must be greater than 0");
        require(amount <= getAvailableBalance(token), "Insufficient balance");
        
        // Update withdrawals
        tokenWithdrawals[token] = tokenWithdrawals[token].add(amount);
        
        // In a real implementation, this would:
        // 1. Unstake tokens from yield protocols
        // 2. Remove liquidity from Mento pools
        // 3. Swap back to requested token if needed
        
        // For demo purposes, we just track the withdrawal
        emit Withdrawn(token, amount);
    }

    /**
     * @dev Calculate and generate yield for all tokens
     */
    function generateYield() external {
        uint256 timeElapsed = block.timestamp.sub(lastYieldCalculation);
        require(timeElapsed >= 1 hours, "Yield calculation too frequent");
        
        address[] memory supportedTokens = TokenConfig.getAllSupportedTokens();
        
        for (uint256 i = 0; i < supportedTokens.length; i++) {
            address token = supportedTokens[i];
            uint256 currentDeposits = tokenDeposits[token].sub(tokenWithdrawals[token]);
            
            if (currentDeposits > 0) {
                // Calculate yield based on APY and time elapsed
                uint256 yieldAmount = calculateYield(token, currentDeposits, timeElapsed);
                
                if (yieldAmount > 0) {
                    tokenYield[token] = tokenYield[token].add(yieldAmount);
                    emit YieldGenerated(token, yieldAmount);
                }
            }
        }
        
        lastYieldCalculation = block.timestamp;
    }

    /**
     * @dev Calculate yield for a specific token and time period
     * @param token Token address
     * @param principal Principal amount
     * @param timeElapsed Time elapsed in seconds
     * @return yieldAmount Calculated yield amount
     */
    function calculateYield(address token, uint256 principal, uint256 timeElapsed) 
        public 
        pure 
        returns (uint256) 
    {
        // Simple interest calculation: yield = principal * rate * time
        // APY_BASIS_POINTS is in basis points (800 = 8%)
        uint256 ratePerSecond = APY_BASIS_POINTS.mul(1e18).div(10000).div(SECONDS_PER_YEAR);
        return principal.mul(ratePerSecond).mul(timeElapsed).div(1e18);
    }

    /**
     * @dev Get available balance for a token (deposits - withdrawals + yield)
     * @param token Token address
     * @return balance Available balance
     */
    function getAvailableBalance(address token) public view returns (uint256) {
        uint256 netDeposits = tokenDeposits[token].sub(tokenWithdrawals[token]);
        uint256 currentYield = getCurrentYield(token);
        return netDeposits.add(currentYield);
    }

    /**
     * @dev Get current yield for a token
     * @param token Token address
     * @return yield Current yield amount
     */
    function getCurrentYield(address token) public view returns (uint256) {
        uint256 timeElapsed = block.timestamp.sub(lastYieldCalculation);
        uint256 netDeposits = tokenDeposits[token].sub(tokenWithdrawals[token]);
        
        if (netDeposits == 0 || timeElapsed == 0) {
            return tokenYield[token];
        }
        
        uint256 newYield = calculateYield(token, netDeposits, timeElapsed);
        return tokenYield[token].add(newYield);
    }

    /**
     * @dev Get total value locked for a token
     * @param token Token address
     * @return tvl Total value locked
     */
    function getTVL(address token) external view returns (uint256) {
        return getAvailableBalance(token);
    }

    /**
     * @dev Get current APY for a token
     * @param token Token address
     * @return apy APY in basis points
     */
    function getAPY(address token) external pure returns (uint256) {
        // In a real implementation, this would calculate APY based on:
        // 1. Current Mento pool rates
        // 2. Liquidity mining rewards
        // 3. Protocol fees
        return APY_BASIS_POINTS; // 8% for demo
    }

    /**
     * @dev Rebalance strategy (placeholder for future implementation)
     * @param fromToken Source token
     * @param toToken Destination token
     * @param amount Amount to rebalance
     */
    function rebalance(address fromToken, address toToken, uint256 amount) 
        external 
        onlyOwner 
        onlyWhitelistedToken(fromToken)
        onlyWhitelistedToken(toToken)
    {
        require(amount > 0, "Amount must be greater than 0");
        require(amount <= getAvailableBalance(fromToken), "Insufficient balance");
        
        // In a real implementation, this would:
        // 1. Withdraw from current position
        // 2. Swap through Mento protocol
        // 3. Deposit into new position
        
        emit Rebalanced(fromToken, toToken, amount);
    }

    /**
     * @dev Get strategy statistics
     * @param token Token address
     * @return deposits Total deposits
     * @return withdrawals Total withdrawals
     * @return yield Total yield generated
     * @return tvl Current TVL
     */
    function getStrategyStats(address token) 
        external 
        view 
        returns (
            uint256 deposits,
            uint256 withdrawals,
            uint256 yield,
            uint256 tvl
        ) 
    {
        deposits = tokenDeposits[token];
        withdrawals = tokenWithdrawals[token];
        yield = getCurrentYield(token);
        tvl = getAvailableBalance(token);
    }

    /**
     * @dev Emergency function to withdraw all funds (only owner)
     * @param token Token address
     */
    function emergencyWithdraw(address token) external onlyOwner {
        uint256 balance = getAvailableBalance(token);
        require(balance > 0, "No funds to withdraw");
        
        tokenWithdrawals[token] = tokenWithdrawals[token].add(balance);
        emit Withdrawn(token, balance);
    }
}

contracts/vault/MultiTokenVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "../libraries/TokenConfig.sol";

/**
 * @title MultiTokenVault
 * @dev Vault supporting multiple tokens (cUSD, USDC, CELO) with yield generation
 */
contract MultiTokenVault is ERC20, ReentrancyGuard, Pausable, Ownable {
    using SafeMath for uint256;

    // Events
    event Deposit(address indexed user, address indexed token, uint256 amount, uint256 shares);
    event Withdraw(address indexed user, address indexed token, uint256 shares, uint256 amount);
    event StrategyUpdated(address indexed oldStrategy, address indexed newStrategy);
    event TokenWhitelisted(address indexed token, bool isWhitelisted);
    event EmergencyWithdraw(address indexed user, address indexed token, uint256 amount);

    // State variables
    mapping(address => bool) public whitelistedTokens;
    mapping(address => mapping(address => uint256)) public userTokenShares; // user => token => shares
    mapping(address => uint256) public totalTokenAssets; // token => total assets
    mapping(address => uint256) public totalTokenShares; // token => total shares
    
    address public strategy;
    uint256 public constant MAX_FEE = 1000; // 10% max fee
    uint256 public depositFee = 0; // 0% deposit fee
    uint256 public withdrawalFee = 0; // 0% withdrawal fee
    uint256 public performanceFee = 200; // 2% performance fee

    // Modifiers
    modifier onlyWhitelistedToken(address token) {
        require(whitelistedTokens[token], "Token not whitelisted");
        _;
    }

    modifier validAmount(uint256 amount) {
        require(amount > 0, "Amount must be greater than 0");
        _;
    }

    constructor(
        string memory name,
        string memory symbol,
        address _strategy
    ) ERC20(name, symbol) {
        strategy = _strategy;
        
        // Whitelist supported tokens
        address[] memory supportedTokens = TokenConfig.getAllSupportedTokens();
        for (uint256 i = 0; i < supportedTokens.length; i++) {
            whitelistedTokens[supportedTokens[i]] = true;
            emit TokenWhitelisted(supportedTokens[i], true);
        }
    }

    /**
     * @dev Deposit tokens into the vault
     * @param token Token address to deposit
     * @param amount Amount to deposit
     */
    function deposit(address token, uint256 amount) 
        external 
        payable 
        nonReentrant 
        whenNotPaused 
        onlyWhitelistedToken(token)
        validAmount(amount)
    {
        TokenConfig.TokenInfo memory tokenInfo = TokenConfig.getTokenInfo(token);
        require(amount >= tokenInfo.minDeposit, "Amount below minimum");
        require(amount <= tokenInfo.maxDeposit, "Amount above maximum");

        uint256 depositAmount = amount;
        
        // Handle native CELO deposits
        if (TokenConfig.isNativeToken(token)) {
            require(msg.value == amount, "Incorrect CELO amount");
        } else {
            // Transfer ERC20 tokens
            IERC20(token).transferFrom(msg.sender, address(this), amount);
        }

        // Calculate shares to mint
        uint256 shares = convertToShares(token, depositAmount);
        require(shares > 0, "Shares must be greater than 0");

        // Apply deposit fee if any
        if (depositFee > 0) {
            uint256 feeAmount = depositAmount.mul(depositFee).div(10000);
            depositAmount = depositAmount.sub(feeAmount);
            shares = convertToShares(token, depositAmount);
        }

        // Update state
        userTokenShares[msg.sender][token] = userTokenShares[msg.sender][token].add(shares);
        totalTokenAssets[token] = totalTokenAssets[token].add(depositAmount);
        totalTokenShares[token] = totalTokenShares[token].add(shares);

        // Mint vault shares
        _mint(msg.sender, shares);

        // Transfer to strategy if not native token
        if (!TokenConfig.isNativeToken(token)) {
            IERC20(token).approve(strategy, depositAmount);
            // Strategy will handle the deposit
        }

        emit Deposit(msg.sender, token, depositAmount, shares);
    }

    /**
     * @dev Withdraw tokens from the vault
     * @param token Token address to withdraw
     * @param shares Shares to burn
     */
    function withdraw(address token, uint256 shares) 
        external 
        nonReentrant 
        whenNotPaused 
        onlyWhitelistedToken(token)
        validAmount(shares)
    {
        require(userTokenShares[msg.sender][token] >= shares, "Insufficient shares");
        require(totalTokenShares[token] >= shares, "Insufficient total shares");

        // Calculate assets to withdraw
        uint256 assets = convertToAssets(token, shares);
        require(assets > 0, "Assets must be greater than 0");

        // Apply withdrawal fee if any
        uint256 feeAmount = 0;
        if (withdrawalFee > 0) {
            feeAmount = assets.mul(withdrawalFee).div(10000);
            assets = assets.sub(feeAmount);
        }

        // Update state
        userTokenShares[msg.sender][token] = userTokenShares[msg.sender][token].sub(shares);
        totalTokenAssets[token] = totalTokenAssets[token].sub(assets.add(feeAmount));
        totalTokenShares[token] = totalTokenShares[token].sub(shares);

        // Burn vault shares
        _burn(msg.sender, shares);

        // Transfer tokens to user
        if (TokenConfig.isNativeToken(token)) {
            payable(msg.sender).transfer(assets);
        } else {
            IERC20(token).transfer(msg.sender, assets);
        }

        emit Withdraw(msg.sender, token, shares, assets);
    }

    /**
     * @dev Convert assets to shares for a specific token
     * @param token Token address
     * @param assets Asset amount
     * @return shares Share amount
     */
    function convertToShares(address token, uint256 assets) public view returns (uint256) {
        if (totalTokenShares[token] == 0) {
            return assets; // 1:1 ratio for first deposit
        }
        return assets.mul(totalTokenShares[token]).div(totalTokenAssets[token]);
    }

    /**
     * @dev Convert shares to assets for a specific token
     * @param token Token address
     * @param shares Share amount
     * @return assets Asset amount
     */
    function convertToAssets(address token, uint256 shares) public view returns (uint256) {
        if (totalTokenShares[token] == 0) {
            return 0;
        }
        return shares.mul(totalTokenAssets[token]).div(totalTokenShares[token]);
    }

    /**
     * @dev Get user's asset balance for a specific token
     * @param user User address
     * @param token Token address
     * @return balance Asset balance
     */
    function userAssetBalance(address user, address token) external view returns (uint256) {
        uint256 shares = userTokenShares[user][token];
        return convertToAssets(token, shares);
    }

    /**
     * @dev Get user's shares for a specific token
     * @param user User address
     * @param token Token address
     * @return shares Share amount
     */
    function userTokenSharesBalance(address user, address token) external view returns (uint256) {
        return userTokenShares[user][token];
    }

    /**
     * @dev Get total assets for a specific token
     * @param token Token address
     * @return assets Total asset amount
     */
    function totalAssets(address token) external view returns (uint256) {
        return totalTokenAssets[token];
    }

    /**
     * @dev Get total shares for a specific token
     * @param token Token address
     * @return shares Total share amount
     */
    function totalShares(address token) external view returns (uint256) {
        return totalTokenShares[token];
    }

    /**
     * @dev Get current APY for a specific token (placeholder - will be implemented by strategy)
     * @param token Token address
     * @return apy APY in basis points (e.g., 800 = 8%)
     */
    function getAPY(address token) external view returns (uint256) {
        // This will be implemented by the strategy contract
        // For now, return a placeholder value
        return 800; // 8% APY
    }

    /**
     * @dev Update strategy contract
     * @param newStrategy New strategy address
     */
    function updateStrategy(address newStrategy) external onlyOwner {
        require(newStrategy != address(0), "Invalid strategy address");
        address oldStrategy = strategy;
        strategy = newStrategy;
        emit StrategyUpdated(oldStrategy, newStrategy);
    }

    /**
     * @dev Whitelist/unwhitelist a token
     * @param token Token address
     * @param isWhitelisted Whether to whitelist the token
     */
    function setTokenWhitelist(address token, bool isWhitelisted) external onlyOwner {
        whitelistedTokens[token] = isWhitelisted;
        emit TokenWhitelisted(token, isWhitelisted);
    }

    /**
     * @dev Set fees
     * @param _depositFee Deposit fee in basis points
     * @param _withdrawalFee Withdrawal fee in basis points
     * @param _performanceFee Performance fee in basis points
     */
    function setFees(
        uint256 _depositFee,
        uint256 _withdrawalFee,
        uint256 _performanceFee
    ) external onlyOwner {
        require(_depositFee <= MAX_FEE, "Deposit fee too high");
        require(_withdrawalFee <= MAX_FEE, "Withdrawal fee too high");
        require(_performanceFee <= MAX_FEE, "Performance fee too high");
        
        depositFee = _depositFee;
        withdrawalFee = _withdrawalFee;
        performanceFee = _performanceFee;
    }

    /**
     * @dev Pause the contract
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause the contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Emergency withdraw function (only when paused)
     * @param token Token address
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(address token, uint256 amount) external whenPaused {
        require(userTokenShares[msg.sender][token] > 0, "No shares to withdraw");
        
        if (TokenConfig.isNativeToken(token)) {
            require(address(this).balance >= amount, "Insufficient CELO balance");
            payable(msg.sender).transfer(amount);
        } else {
            require(IERC20(token).balanceOf(address(this)) >= amount, "Insufficient token balance");
            IERC20(token).transfer(msg.sender, amount);
        }
        
        emit EmergencyWithdraw(msg.sender, token, amount);
    }

    /**
     * @dev Receive function for CELO deposits
     */
    receive() external payable {
        // Only allow CELO deposits through the deposit function
        revert("Use deposit function for CELO");
    }
}

